{% if true %}
  def self.delete(client : HTTP::Client, publication_id : String, id : String)
    response = client.delete("/v2/publications/#{publication_id}/#{"{{ (@type.id.gsub(/Beehiiv::/, "")).underscore.gsub(/::/, "/") }}"}s/#{id}")

    if response.status_code == 204
      true
    else
      raise Error.from_json(response.body, "error")
    end
  end

  def self.delete({{ ((@type.id.gsub(/Beehiiv::/, "")).downcase.underscore.gsub(/::/, "_")).id }} : {{ @type.id }})
    delete({{ ((@type.id.gsub(/Beehiiv::/, "")).downcase.underscore.gsub(/::/, "_")).id }}.id)
  end
{% end %}

{% if true %}
  def self.retrieve(client : HTTP::Client, publication_id : String, {{ *arguments }}) : Object({{ @type.id }})
    response = client.get("/v2/publications/#{publication_id}/#{"{{ (@type.id.gsub(/Beehiiv::/, "")).underscore.gsub(/::/, "/") }}"}s/#{id}?expand=stats")

    if response.status_code == 200
      Object({{ @type.id }}).from_json(response.body)
    else
      raise Error.from_json(response.body, "error")
    end
  end

  def self.retrieve({{ ((@type.id.gsub(/Beehiiv::/, "")).downcase.underscore.gsub(/::/, "_")).id }} : {{ @type.id }})
    retrieve({{ ((@type.id.gsub(/Beehiiv::/, "")).downcase.underscore.gsub(/::/, "_")).id }}.id)
  end
{% end %}
